package model;
public class User {
    private String name;
    private String password;
    private String path;
    private int bestScore;
    private int totalGames;
    private int totalWins;
    private int totalLoses;

    public User (String name, String password, String path) {
        this.name = name;
        this.password = password;
        this.path = path;
        this.bestScore = 0;
        this.totalGames = 0;
        this.totalWins = 0;
        this.totalLoses = 0;
    }

    public String getName() {
        return name;
    }
    public String getPassword() {
        return password;
    }
    public String getPath() {
        return path;
    }
    public int getBestScore() {
        return bestScore;
    }
    public void setBestScore(int bestScore) {
        this.bestScore = bestScore;
    }
    public int getTotalGames() {
        return totalGames;
    }
    public void setTotalGames(int totalGames) {
        this.totalGames = totalGames;
    }
    public int getTotalWins() {
        return totalWins;
    }
    public void setTotalWins(int totalWins) {
        this.totalWins = totalWins;
    }
    public int getTotalLoses() {
        return totalLoses;
    }
    public void setTotalLoses(int totalLoses) {
        this.totalLoses = totalLoses;
    }
    public void refreshInfo(int bestScore, int totalGames, int totalWins, int totalLoses) {
        this.bestScore = bestScore;
        this.totalGames = totalGames;
        this.totalWins = totalWins;
        this.totalLoses = totalLoses;
    }
}

package model;
import util.Saver;
import java.io.IOException;
import java.util.*;
public class UserManager {
    private List<User> userList;
    private static final String savePath = "src/main/resources/general";

    public UserManager() {
        try {
            Saver.makeDir(savePath); // 创建存储用户信息的文件夹
        } catch (Exception e) {
            e.printStackTrace();
        }
        userList = new ArrayList<>();
    }

    public User register(String name, String password) throws IOException {
        User user = null;
        if (hasUser(name)) { // 检查用户是否已存在
            throw new IllegalArgumentException("This user has existed!");
        } else if (!isPasswordValid(password)) { // 检查密码是否有效
            throw new IllegalArgumentException("Invalid password!" + "Your password should contain at least 8 characters.");
        } else if (name.equals("") || password.equals("")) { // 检查用户名和密码是否为空
            throw new IllegalArgumentException("Username or password cannot be empty!");
        } else {
            user = new User(name, password, "src/main/resources/savedata/" + name);
            Saver.makeDir(user.getPath());
            userList.add(user);
            // 保存用户信息
            Saver.saveToJson(Saver.buildGson(this), savePath + "/userInfo.json");

        }
        return user;

    }
    public User login(String name, String password) {
        if (userList.isEmpty()) {
            return null;
        } else {
            for (int i = 0; i < userList.size(); i++) {
                if (userList.get(i).getName().equals(name)) {
                    if (!isPasswordCorrect(name, password)) {
                        throw new IllegalArgumentException("Incorrect password!");
                    } else {
                        return userList.get(i);
                    }
                }
            }
            throw new IllegalArgumentException("This user doesn't exist!");
        }
    }
    public void deleteUser(User user) throws IOException {
        userList.remove(user);
        Saver.saveToJson(Saver.buildGson(this), savePath);

    }
    public boolean hasUser(String name) {
        boolean hasUser = false;
        for (int i = 0; i < userList.size(); i++) {
            if (userList.get(i).getName().equals(name)) {
                hasUser = true;
                break;
            }
        }
        return hasUser;
    }
    public boolean isPasswordValid(String password) {
        boolean isPasswordValid = false;
        if (password.length() >= 8) {
            isPasswordValid = true;
        }
        return isPasswordValid;
    }
    public boolean isPasswordCorrect(String name, String password) {
        boolean isPasswordCorrect = false;
        int i0 = 0;
        for (int i = 0; i < userList.size(); i++) {
            if (userList.get(i).getName().equals(name)) {
                i0 = i;
                break;
            }
        }
        if (password.equals(userList.get(i0).getPassword())) {
            isPasswordCorrect = true;
        }
        return isPasswordCorrect;
    }
}

